import React from "react";
import Modal from "react-responsive-modal";
import "./contactUs.css";
import { connect } from "react-redux";
import axios from "../../axios/axios-blogs";
import Spinner from "../UI/Spinner/Spinner";
import { confirmAlert } from "react-custom-confirm-alert";

class ContactUs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: "",
      familyname: "",
      email: "",
      phone: "",
      title: "",
      message: "",
      loading: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.resetForm = this.resetForm.bind(this);
  }

  handleChange(e, type) {
    this.setState({ [type]: e.target.value });
  }

  resetForm() {
    this.setState({
      firstname: "",
      familyname: "",
      email: "",
      phone: "",
      title: "",
      message: ""
    });
  }

  handleSubmit(e) {
    const { firstname, familyname, email, phone, title, message } = this.state;
    e.preventDefault();
    this.setState({ loading: true });
    axios({
      method: "POST",
      url: "/api/contact/contact",
      data: {
        firstname: firstname,
        familyname: familyname,
        email: email,
        phone: phone,
        title: title,
        message: message
      },
      headers: ""
    })
      .then(response => {
        this.setState({ loading: false });
        if (response.data.msg === "success") {
          confirmAlert({
            message:
              "Your message has been sent successfully. We will reply you soon",
            buttons: [{ label: "OK" }]
          });
          this.resetForm();
          this.props.onSwitchContactModal();
        } else if (response.data.msg === "fail") {
          confirmAlert({
            message:
              "Sorry, your message was failed to delivered. Please try again",
            buttons: [{ label: "OK" }]
          });
        }
      })
      .catch(e => {
        this.setState({ loading: false });
      });
  }

  render() {
    const modalBg = {
      modal: {
        width: "570px",
        background: "white",
        borderRadius: "20px",
        overflow: "hidden",
        position: "relative",
        padding: "0px"
      }
    };
    let contactUsForm = (
      <div>
        <section className="contact-title-wrap">
          <span className="contact-title-1">Contact Us</span>
          <span className="contact-title-2">
            We look forward to your message!
          </span>
        </section>
        <form className="contact-form" onSubmit={this.handleSubmit}>
          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Family Name<i style={{ color: "red" }}>*</i> :</div>
            <div className="form-group">
              <div className="media-content">
                <input
                  className="contact-input"
                  placeholder="Enter your surname"
                  type="text"
                  required
                  onChange={e => this.handleChange(e, "familyname")}
                  value={this.state.familyname}
                />
              </div>
            </div>
          </article>

          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Given Name<i style={{ color: "red" }}>*</i> :</div>
            <div className="form-group">
              <div className="media-content">
                <input
                  className="contact-input"
                  placeholder="Enter your given name"
                  type="text"
                  required
                  onChange={e => this.handleChange(e, "firstname")}
                  value={this.state.firstname}
                />
              </div>
            </div>
          </article>

          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Email<i style={{ color: "red" }}>*</i> :</div>
            <div className="form-group">
              <div className="media-content">
                <input
                  className="contact-input"
                  placeholder="Enter your email address"
                  type="email"
                  pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                  required
                  onChange={e => this.handleChange(e, "email")}
                  value={this.state.email}
                />
              </div>
            </div>
          </article>

          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Phone:</div>
            <div className="form-group">
              <div className="media-content">
                <input
                  className="contact-input"
                  placeholder="Enter your phone number"
                  type="tel"
                  onChange={e => this.handleChange(e, "phone")}
                  value={this.state.phone}
                />
              </div>
            </div>
          </article>

          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Title<i style={{ color: "red" }}>*</i> :</div>
            <div className="form-group">
              <div className="media-content">
                <input
                  className="contact-input"
                  placeholder="Enter a title of the message"
                  type="text"
                  required
                  onChange={e => this.handleChange(e, "title")}
                  value={this.state.title}
                />
              </div>
            </div>
          </article>

          <article className="media media-contact" id="QueryInput">
            <div className="media-left form-label">Message<i style={{ color: "red" }}>*</i> :</div>
            <div className="form-group">
              <div className="media-content">
                <textarea
                  className="contact-input contact-input-textarea"
                  placeholder="Comment here.."
                  type="text"
                  required
                  onChange={e => this.handleChange(e, "message")}
                  value={this.state.message}
                  style={{ resize: "vertical" }}
                />
              </div>
            </div>
          </article>

          <div style={{ width: "100%", textAlign: "center" }}>
            <button
              type="submit"
              className="button is-primary is-rounded is-medium"
              style={{ display: "inline-block" }}
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    );

    if (this.state.loading) {
      contactUsForm = (
        <div
          style={{
            textAlign: "center",
            paddingTop: "25%",
            paddingBottom: "25%"
          }}
        >
          <Spinner />
        </div>
      );
    }

    return (
      <Modal
        open={this.props.contactUsOpen}
        onClose={this.props.onSwitchContactModal}
        styles={modalBg}
        center
      >
        {contactUsForm}
      </Modal>
    );
  }
}

const mapStateToProps = state => {
  return {
    contactUsOpen: state.persistedReducer.contactUsOpen
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onSwitchContactModal: () => dispatch({ type: "CONTACTMODAL" })
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ContactUs);
