import React, { Component } from 'react'
import algoliasearch from "algoliasearch";
import "instantsearch.css/themes/algolia.css";
import {
  InstantSearch,
  Highlight,
  connectHits,
  Configure
} from 'react-instantsearch-dom';
import CustomSearchBox from './searchbox'
import Flexbox from 'flexbox-react';

const searchClient = algoliasearch(
  process.env.REACT_APP_APPLICATION_ID,
  process.env.REACT_APP_SEARCH_ADMIN_API
);

const HitComponent = ({ hit, handleSelect }) => {
  return (
    <div className="column box" style={styles.hit}>
      <div 
        onClick={() => handleSelect(hit.name)}
      >
        <Highlight attribute="name" hit={hit} />
      </div>
    </div>
  );
}

const MyHits = connectHits(({ hits, handleSelect }) => {
  const hs = hits.map(hit => <HitComponent 
                                key={hit.objectID} 
                                hit={hit} 
                                handleSelect={handleSelect}/>);
  return <div id="hits">{hs}</div>;
})

export default class Tagsearch extends Component {

  constructor(props) {
    super(props);

    this.state = {
      inputContent:"",
    };
  }

  render() {

    let inputTags = (
      this.props.tags.map((tag) => 
        <li key={tag} style={styles.items}>
          {tag}
          <button
            onClick={() => this.props.handleRemoveItem(tag)}
          >
            (x)
          </button>
        </li>
      )
    )

    let result = (
      <div className="container-fluid" id="results">

      </div>
    )

    if (this.props.hitsDisplay) {
      result = (
        <Flexbox 
          flexDirection="column" 
          minHeight="100vh"
        >
          <div className="rows">
            <MyHits handleSelect={this.props.handleSelect}/>
          </div>
        </Flexbox>
      )
    }

    return (
      <InstantSearch indexName="tags" searchClient>
        <Configure hitsPerPage={12} analytics={true} distinct />
        <CustomSearchBox
          styles={styles}
          openDisplay={this.props.openDisplay}
          closeDisplay={this.props.closeDisplay}
        />
        <Flexbox>
          {inputTags}
        </Flexbox>
        {result}

      </InstantSearch>
    )
  }
}

const styles = {
  container: {
    border: '1px solid #ddd',
    padding: '5px',
    borderRadius: '5px',
  },

  hitStyle: {
    margin: "3% 1% 0 1%"
  },

  input: {
    outline: 'none',
    border: 'none',
    fontSize: '14px',
    fontFamily: 'Helvetica, sans-serif'
  },

  items: {
    display: 'inline-block',
    padding: '2px',
    border: '1px solid blue',
    fontFamily: 'Helvetica, sans-serif',
    borderRadius: '5px',
    marginRight: '5px',
    cursor: 'pointer'
  },

  hit: {
    width: '30%',
    height: '10%',
    float: 'left',
    marginBottom: '10px',
    borderBottom: 'solid 1px #eee',
    margin: '0.5%',
    border: 'solid 1px #eee',
    boxShadow: '0 0 3px #f6f6f6',
    position: 'relative',
    fontSize: '14px'
  }
}
