import React, { Component } from 'react'
import Modal from "react-responsive-modal";
import { connect } from "react-redux";
import { FacebookShareButton, TwitterShareButton } from 'react-share';

const modalBg = {
  modal: {
    background: "white ",
    borderRadius: "10%",
    maxHeight: "43%",
    height: "100%",
    maxWidth: "30%",
    width: "100%"
  }
};

class Share extends Component {
  render() {

    let url = `${process.env.REACT_APP_CLIENT_SERVER}/blog/${this.props.pageID}`

    let shareWindow = (
      <div>
        <input 
          className="input is-rounded"
          type="text"
          defaultValue={url}

        />
        <FacebookShareButton url={url}/>
        <TwitterShareButton url={url}/>
      </div>
    )

    return (
      <Modal
        className="model-share"
        open={this.props.shareOpen}
        onClose={this.props.onSwitchShareModal}
        styles={modelBg}
      >
        {shareWindow}
      </Modal>
    )
  }
}

const mapStateToProps = state => {
  return {
    shareOpen: state.persistedReducer.shareOpen
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSwitchShareModal: () => dispatch({ type: "SHAREMODAL" })
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Share);
