import React, { Component } from 'react'
import { Highlight, connectHits } from 'react-instantsearch-dom';

const HitComponent = ({hit, handleSelect}) => {
  return (
    <div className="column box" style={styles.hit}>
      <div 
        onClick={() => handleSelect(hit.name)}
      >
        <Highlight attribute="name" hit={hit} />
      </div>
    </div>
  );
}

const customHits = ({ hits, handleSelect, ...props }) => {
  const hs = hits.map(hit => <HitComponent 
                              key={hit.objectID} 
                              hit={hit} 
                              handleSelect={handleSelect}/>);
  return <div id="hits">{hs}</div>;
}

const styles = {
  hit: {
    width: '30%',
    height: '10%',
    float: 'left',
    marginBottom: '10px',
    borderBottom: 'solid 1px #eee',
    margin: '0.5%',
    border: 'solid 1px #eee',
    boxShadow: '0 0 3px #f6f6f6',
    position: 'relative',
    fontSize: '14px'
  }
}

export default connectHits(customHits)
