import React, { Component } from 'react'
import Modal from "react-responsive-modal";
import { connect } from "react-redux";
import { Passers } from "prop-passer";

import { 
  FacebookShareButton, 
  TwitterShareButton,
  FacebookIcon,
  TwitterIcon
} from 'react-share';

class Share extends Component {
  render() {

    const modalBg = {
      modal: {
        background: "white ",
        borderRadius: "10%",
        maxHeight: "43%",
        height: "100%",
        maxWidth: "30%",
        width: "100%"
      }
    };

    const shareBar = {
      shareBarCss: {
        display: "block",
        textAlign: "center",
        padding: "1rem 1.1rem 0.8rem 1rem",
        transition: "all 0.6s ease",
        color: "white",
        fontSize: "2rem",
        marginRight: "0.01rem"
      }
    }

    const shareItem = {
      shareItemCss: {
        float: "left",
        display: "inline"
      }
    }

    const {
      url = String(window.location),
      title = this.props.title,
      size = 40,
    } = this.props;

    const ShareList = Passers({
      url
    })({
      title: `Share ${String(window.location)}`
    })({
      styles: { shareItem }
    })("li");

    let shareWindow = (
      <div>
        <input 
        className="input is-rounded"
        type="text"
        defaultValue={url}
        />
        <div
          className="share-bt"
          styles={shareBar}
        >
          <ShareList>
            <FacebookShareButton
              quote={title}
            >
              <FacebookIcon size={size} />
            </FacebookShareButton>
            <TwitterShareButton
              quote={title}
            >
              <TwitterIcon size={size} />
            </TwitterShareButton>
          </ShareList>
        </div>
      </div>
    )

    return (
      <Modal
        className="model-share"
        open={this.props.shareOpen}
        onClose={this.props.onSwitchShareModal}
        styles={modalBg}
      >
        {shareWindow}
      </Modal>
    )
  }
}

const mapStateToProps = state => {
  return {
    shareOpen: state.persistedReducer.shareOpen
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSwitchShareModal: () => dispatch({ type: "SHAREMODAL" })
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Share);
