import React, { Component } from 'react'
import Modal from "react-responsive-modal";
import { connect } from "react-redux";
import { Passers } from "prop-passer";
import styled from 'styled-components'

import { 
  FacebookShareButton, 
  TwitterShareButton,
  FacebookIcon,
  TwitterIcon
} from 'react-share';

class Share extends Component {
  render() {

    const modalBg = {
      modal: {
        background: "white ",
        borderRadius: "10%",
        maxHeight: "43%",
        height: "100%",
        maxWidth: "30%",
        width: "100%"
      }
    };

    const SubShareCSS = styled.ul.attrs({
      id: 'sub-share',
      className: 'x-margin x-padding flex flex--row flex--center height-percent'
    })`
      // .share-custom {
      //     display: block;
      //     text-align: center;
      //     padding: 1rem 1.1rem 0.8rem 1rem;
      //     transition: all 0.6s ease;
      //     color: white;
      //     font-size: 2rem;
      //     margin-right: 0.01rem;
      // }
    `

    const {
      url = String(window.location),
      title = this.props.title,
      size = "2.5rem",
    } = this.props;

    const ShareList = Passers({
      url,
      className: "network__share-button",
    })({
      className: "network cursor-pointer hover transition--default",
      title: `Share ${String(window.location)}`,
    })("li");

    let shareWindow = (
      <div>
        <input 
        className="input is-rounded"
        type="text"
        defaultValue={url}
        />
        <SubShareCSS>
          <ShareList>
            <FacebookShareButton
              quote={title}
            >
              <FacebookIcon size={size} />
            </FacebookShareButton>
            <TwitterShareButton
              quote={title}
            >
              <TwitterIcon size={size} />
            </TwitterShareButton>
          </ShareList>
        </SubShareCSS>
      </div>
    )

    return (
      <Modal
        className="model-share"
        open={this.props.shareOpen}
        onClose={this.props.onSwitchShareModal}
        styles={modalBg}
      >
        {shareWindow}
      </Modal>
    )
  }
}

const mapStateToProps = state => {
  return {
    shareOpen: state.persistedReducer.shareOpen
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSwitchShareModal: () => dispatch({ type: "SHAREMODAL" })
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Share);
